import{_ as t,c as r,o as a,ae as s}from"./chunks/framework.C4qgxD4H.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"species/isle_animals.md","filePath":"docs/species/isle_animals.md"}'),n={name:"species/isle_animals.md"};function i(o,e,l,h,g,d){return a(),r("div",null,e[0]||(e[0]=[s('<h4 id="_1-starwing-doves" tabindex="-1"><strong>1. Starwing Doves</strong> <a class="header-anchor" href="#_1-starwing-doves" aria-label="Permalink to &quot;**1. Starwing Doves**&quot;">​</a></h4><ul><li><strong>Description</strong>: Birds with shimmering feathers that resemble constellations. Their wings glow faintly at night, making them a symbol of guidance and hope.</li><li><strong>Habitat</strong>: Commonly found roosting in the spires of Lumina Caelora and the forests of Silvaspire.</li><li><strong>Behavior</strong>: Known for their calming presence, they are often trained as messenger birds by the Ulversians.</li><li><strong>Lore</strong>: Considered sacred, they are said to be born from fragments of stardust that fell to the Isles.</li></ul><hr><h4 id="_2-lumisprites" tabindex="-1"><strong>2. Lumisprites</strong> <a class="header-anchor" href="#_2-lumisprites" aria-label="Permalink to &quot;**2. Lumisprites**&quot;">​</a></h4><ul><li><strong>Description</strong>: Tiny, glowing, dragonfly-like creatures with crystalline wings. They emit a soft hum and leave trails of light as they fly.</li><li><strong>Habitat</strong>: Thrive in the glowing forests of Silvaspire and Etheren’s magical gardens.</li><li><strong>Behavior</strong>: Attracted to magic users, often aiding them in casting spells or warning of danger.</li><li><strong>Lore</strong>: Lumisprites are believed to be manifestations of excess Ulverse energy, flitting between realms.</li></ul><hr><h4 id="_3-etherian-gliders" tabindex="-1"><strong>3. Etherian Gliders</strong> <a class="header-anchor" href="#_3-etherian-gliders" aria-label="Permalink to &quot;**3. Etherian Gliders**&quot;">​</a></h4><ul><li><strong>Description</strong>: Large, manta ray-like creatures that &quot;swim&quot; through the air. They have translucent, glowing skin and are often mistaken for clouds.</li><li><strong>Habitat</strong>: The skies around Etheren and Caelum Spire.</li><li><strong>Behavior</strong>: Peaceful and slow-moving, they are sometimes ridden by Ulversians using harnesses.</li><li><strong>Lore</strong>: Legends claim that Etherian Gliders are fragments of the first skies created during the Primordial Era.</li></ul><hr><h4 id="_4-chronohounds" tabindex="-1"><strong>4. Chronohounds</strong> <a class="header-anchor" href="#_4-chronohounds" aria-label="Permalink to &quot;**4. Chronohounds**&quot;">​</a></h4><ul><li><strong>Description</strong>: Sleek canine creatures with metallic fur and glowing blue eyes. They can manipulate time slightly, enabling them to appear as if they are teleporting short distances.</li><li><strong>Habitat</strong>: Found near the Caelum Spire or guarding significant locations like Ferryn’s Bridge.</li><li><strong>Behavior</strong>: Loyal and intelligent, they are often trained by Watchers to act as guardians.</li><li><strong>Lore</strong>: Chronohounds are said to have been created by early Watchers to assist in monitoring universal security.</li></ul><hr><h4 id="_5-auranthian-tradersnakes" tabindex="-1"><strong>5. Auranthian Tradersnakes</strong> <a class="header-anchor" href="#_5-auranthian-tradersnakes" aria-label="Permalink to &quot;**5. Auranthian Tradersnakes**&quot;">​</a></h4><ul><li><strong>Description</strong>: Small serpents with golden scales that shimmer like coins. They are non-venomous and highly intelligent, capable of mimicking sounds they hear.</li><li><strong>Habitat</strong>: Found in the marketplaces of Auranthia, often coiled around merchants’ wares.</li><li><strong>Behavior</strong>: Tradersnakes are known for their curiosity and have been seen helping merchants by fetching small items.</li><li><strong>Lore</strong>: It’s said that they bring good fortune and prosperity to anyone who earns their loyalty.</li></ul><hr><h3 id="sea-creatures-of-the-sea-of-eternity" tabindex="-1"><strong>Sea Creatures of the Sea of Eternity</strong> <a class="header-anchor" href="#sea-creatures-of-the-sea-of-eternity" aria-label="Permalink to &quot;**Sea Creatures of the Sea of Eternity**&quot;">​</a></h3><h4 id="_1-eternus-leviathans" tabindex="-1"><strong>1. Eternus Leviathans</strong> <a class="header-anchor" href="#_1-eternus-leviathans" aria-label="Permalink to &quot;**1. Eternus Leviathans**&quot;">​</a></h4><ul><li><strong>Description</strong>: Massive, whale-like creatures with glowing patterns along their bodies. Their songs resonate with magical frequencies.</li><li><strong>Habitat</strong>: Deep within the Sea of Eternity, occasionally surfacing near Oceanora.</li><li><strong>Behavior</strong>: Gentle giants that feed on cosmic plankton, they are revered by Mechromorphs as protectors of the sea.</li><li><strong>Lore</strong>: Legends say Eternus Leviathans were the first beings to drink from the Sea of Eternity, granting them their immense size and long lifespan.</li></ul><hr><h4 id="_2-crystalback-turtles" tabindex="-1"><strong>2. Crystalback Turtles</strong> <a class="header-anchor" href="#_2-crystalback-turtles" aria-label="Permalink to &quot;**2. Crystalback Turtles**&quot;">​</a></h4><ul><li><strong>Description</strong>: Large turtles with shells made of glittering Cosmora Crystals. Their shells absorb and store energy, which they release as dazzling light when threatened.</li><li><strong>Habitat</strong>: Found along the shores of Oceanora and around Machina Aeternum.</li><li><strong>Behavior</strong>: Peaceful and slow-moving, they are often accompanied by smaller creatures seeking safety.</li><li><strong>Lore</strong>: They are believed to have a special connection to the Cosmora Forge and are seen as symbols of resilience.</li></ul><hr><h4 id="_3-starlight-eels" tabindex="-1"><strong>3. Starlight Eels</strong> <a class="header-anchor" href="#_3-starlight-eels" aria-label="Permalink to &quot;**3. Starlight Eels**&quot;">​</a></h4><ul><li><strong>Description</strong>: Long, glowing eels with fins that sparkle like stars. They can generate electric currents and are often used to power small devices.</li><li><strong>Habitat</strong>: Underwater caves near Oceanora.</li><li><strong>Behavior</strong>: Curious but skittish, they avoid conflict unless provoked.</li><li><strong>Lore</strong>: Many believe these eels are fragments of electricity magic left behind by early experiments in Oceanora.</li></ul><hr><h3 id="mechanical-and-hybrid-creatures" tabindex="-1"><strong>Mechanical and Hybrid Creatures</strong> <a class="header-anchor" href="#mechanical-and-hybrid-creatures" aria-label="Permalink to &quot;**Mechanical and Hybrid Creatures**&quot;">​</a></h3><h4 id="_1-gearhawks" tabindex="-1"><strong>1. Gearhawks</strong> <a class="header-anchor" href="#_1-gearhawks" aria-label="Permalink to &quot;**1. Gearhawks**&quot;">​</a></h4><ul><li><strong>Description</strong>: Mechanical birds with wings made of rotating gears and powered by small Cosmora Crystals.</li><li><strong>Habitat</strong>: Commonly found in Machina Aeternum, perched atop towers or assisting Mechromorphs.</li><li><strong>Behavior</strong>: Programmed for various tasks, such as delivering messages or scouting.</li><li><strong>Lore</strong>: Created by Eon as one of the first autonomous Mechromorph constructs.</li></ul><hr><h4 id="_2-automata-stags" tabindex="-1"><strong>2. Automata Stags</strong> <a class="header-anchor" href="#_2-automata-stags" aria-label="Permalink to &quot;**2. Automata Stags**&quot;">​</a></h4><ul><li><strong>Description</strong>: Majestic deer-like constructs with antlers made of glowing crystal. Their bodies are metallic, yet they move with organic grace.</li><li><strong>Habitat</strong>: Found wandering between Silvaspire and Machina Aeternum.</li><li><strong>Behavior</strong>: Peaceful creatures, often sought out by Mechromorphs and Ulverse Elves for companionship.</li><li><strong>Lore</strong>: Automata Stags are said to represent the harmony between nature and technology.</li></ul><hr><h4 id="_3-shardbeasts" tabindex="-1"><strong>3. Shardbeasts</strong> <a class="header-anchor" href="#_3-shardbeasts" aria-label="Permalink to &quot;**3. Shardbeasts**&quot;">​</a></h4><ul><li><strong>Description</strong>: Wolf-like creatures with bodies partially composed of shattered Cosmora Crystals. Their glowing forms flicker and shift, making them appear ghostly.</li><li><strong>Habitat</strong>: Found in abandoned mines and ruins across the Ulverse Isles.</li><li><strong>Behavior</strong>: Aggressive and territorial, they guard crystal deposits but rarely attack unless provoked.</li><li><strong>Lore</strong>: Shardbeasts are thought to be failed experiments in fusing organic life with Cosmora energy.</li></ul><hr><h3 id="inhabitants-of-the-forests-and-plains" tabindex="-1"><strong>Inhabitants of the Forests and Plains</strong> <a class="header-anchor" href="#inhabitants-of-the-forests-and-plains" aria-label="Permalink to &quot;**Inhabitants of the Forests and Plains**&quot;">​</a></h3><h4 id="_1-verdant-golems" tabindex="-1"><strong>1. Verdant Golems</strong> <a class="header-anchor" href="#_1-verdant-golems" aria-label="Permalink to &quot;**1. Verdant Golems**&quot;">​</a></h4><ul><li><strong>Description</strong>: Plant-based humanoid creatures with glowing energy cores embedded in their chests.</li><li><strong>Habitat</strong>: Silvaspire’s forests and occasionally near Etheren.</li><li><strong>Behavior</strong>: Slow-moving and gentle, they act as protectors of the forests, nurturing the land and its energy.</li><li><strong>Lore</strong>: Ulverse Elves believe these golems were created during the Ulverse Era to guard sacred energy pools.</li></ul><hr><h4 id="_2-whisperfoxes" tabindex="-1"><strong>2. Whisperfoxes</strong> <a class="header-anchor" href="#_2-whisperfoxes" aria-label="Permalink to &quot;**2. Whisperfoxes**&quot;">​</a></h4><ul><li><strong>Description</strong>: Small, fox-like creatures with silken fur that seems to shimmer in the light. They are known for their ability to &quot;whisper&quot; messages over long distances.</li><li><strong>Habitat</strong>: Common around Silvaspire and the outskirts of Lumina Caelora.</li><li><strong>Behavior</strong>: Intelligent and playful, they often bond with Ulversian children.</li><li><strong>Lore</strong>: Whisperfoxes are thought to be messengers of the Ulverse Core itself, delivering its will to the Isles.</li></ul>',41)]))}const m=t(n,[["render",i]]);export{u as __pageData,m as default};
